function Evento({ numero }) {

    function meuEvento() {
        /*Evento que imprime o evento. Importante destacar que
        consigo concatenar a variável dentro de minha string, usando  */
        console.log(`Opa, fui ativado! ${numero}`)
    }

    return (
        <div>
            <p>Clique para disparar um evento: </p>
            {/* Criando um evento quando clico em um botão */}
            <button onClick={meuEvento}>Ativar!</button>
        </div>
    )
}

export default Evento
