import { useState } from 'react'

function Form() {

    function cadastrarUsuario(e) {
        /*Usamos esta função para prevenir que se comporte como um formulário padrão, mandando
        pro back-dend, pois no momento não estamos mexendo com um bd e simplesmente printar a informação no log */
        e.preventDefault()
        console.log(name)
        console.log("Cadastrou o usuário")
    }

    const [name, setName] = useState()
    const [password, setPassword] = useState()

    return (
        <div>
            <h1>Meu cadastro:</h1>
            {/* evento para trabalhar com formulários */}
            <form onSubmit={cadastrarUsuario}>
                <div>
                    <label htmlFor="name">Nome: </label>
                    <input type="text" id="name" name="name" placeholder="Digite o seu nome"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <div>
                    <label htmlFor="password">Senha: </label>
                    <input type="password" id="password" name="password" placeholder="Digite a sua senha"
                    onChange={(e) => setPass}
                    />
                </div>
                <div>
                    <input type="submit" value="Cadastrar" />
                </div>
            </form>
        </div>
    )
}

export default Form
