import { useState } from 'react';
import './App.css';
import Saudacao from './components/aula-14/Saudacao';
import SeuNome from './components/aula-14/SeuNome';




function App() {
  /*Perceba que agora o useState fica dentro do App */
  const [nome, setNome] = useState();
  return (
    <div className="App">
      <h1>State Lift</h1>

      <SeuNome setNome={setNome}/>
      <Saudacao nome={nome}/>
    </div>
  );
}
export default App;
