import logo from './logo.svg';
import './App.css';

function App() {
  const name = "Andre";
  const nameUpper = name.toUpperCase();

function sum(a, b){
  return a+b;
}


/* url para gerar imagem de 150x150 */
const url = "https://via.placeholder.com/150";

  return (
    <div className="App">
      <h2>Alterando JSX</h2>
      {/* usando função para alterar variável */}
      <p>Olá, {nameUpper}!</p>
      <p>Soma: {2+2}</p>
      {/* usando função para soma */}
      <p>Soma com função: {sum(1, 2)}</p>
      {/* tag img tem q ter a barra no final senão o jsx reclama do fechamento */}
      <img src={url} alt="Minha imagem" />
    </div>
  );
}

export default App;
